    &XMLWhenFilterLoading;
    &XMLWhenFilterClosing;
    
      
        
declare @accountName nvarchar(1024), @accountName2 nvarchar(1024), @customerName nvarchar(1024), @customerName2 nvarchar(1024)
select @accountName = ten_tk, @accountName2 = ten_tk2 from dmtk where tk = @tk
select @customerName = ten_kh, @customerName2 = ten_kh2 from dmkh where ma_kh = @ma_kh
select cast(@tu_ngay as smalldatetime) as date_from, cast(@den_ngay as smalldatetime) as date_to,
		@ma_kh as ma_kh, @customerName as ten_kh, @customerName2 as ten_kh2, @tk as tk, @accountName as ten_tk, @accountName2 as ten_tk2 
exec rs_rptDCCustomer @tu_ngay, @den_ngay, @ma_dvcs, @tk, @ma_kh, @@language, @isbalance, @isdetail, 2, @@userID, @@admin

      
    
  

  
    
      &OutlineEntry;
      
function active$VoucherFilter$(sender) {
	sender.add_onResponseComplete(on$Filter$ResponseComplete);
	changeLookupReadonly(sender, 'ma_dvcs');
	sender.request('GetDefaultValue', 'SetDefaultValue', ['tk', 'ma_kh']);
}
function close$VoucherFilter$(sender) {try {sender.remove_onResponseComplete(on$Filter$ResponseComplete);} catch (ex) {}}
function on$Filter$ResponseComplete(sender, e) {
  var f = e.object, context = e.type.Context, result = e.type.Result;
  switch (context) {
    case 'Checking':		
			var g = f.grid;
			var x = f.getItemValue('mau_bc'), b = f.getItemValue('isbalance'), d = f.getItemValue('isdetail');
      f.grid._hiddenFields = [
				{Fields: 'so_luong, gia, tien', Value: (d == '0')},
				{Fields: 'du_no, du_co', Value: (b == '0')},
				{Fields: 'gia_nt, tien_nt', Value: (x == '10' || d == '0')},
				{Fields: 'du_no_nt, du_co_nt', Value: (x == '10' || b == '0')},
				{Fields: 'ps_no_nt, ps_co_nt, ty_gia, ty_gia_ck, ma_nt', Value: (x == '10')}	
			];
      g._hiddenForms = [
        {Form: [4, 5, 6, 7], Value: (d == '0')},
        {Form: [2, 3, 6 ,7], Value: (b == '0')},
        {Form: [0, 1, 2, 3], Value: (d == '1')},
        {Form: [0, 1, 4, 5], Value: (b == '1')}
      ];
			var dFrom = f.getItem('tu_ngay').value, dTo = f.getItem('den_ngay').value, account = f.getItem('tk').value, customer = f.getItem('ma_kh').value, customerName = f.getItem('ten_kh%l').value;
      g._alterTitle = [null, [['%d1', dFrom, true], ['%d2', dTo, true], ['%s1', account, true], ['%s2', customer, true], ['%s3', customerName, true]]];
      break;
		case 'SetDefaultValue':
      f.setItemControlBehavior('ten_tk%l', result[0].Value, null, true);
      if (result[0].Value == '') f.setItemValue('tk', '');
			f.setItemControlBehavior('ten_kh%l', result[1].Value, null, true);
      if (result[1].Value == '') f.setItemValue('ma_kh', '');
			break;
		default:
      break;
  }
}

    
  

  
    
      
        
declare @c0 nvarchar(1024), @c1 nvarchar(1024)
select @c0 = ten_tk%l from dmtk where tk = @tk
select @c1 = ten_kh%l from dmkh where ma_kh = @ma_kh
select isnull(@c0, '') as c0, isnull(@c1, '') as c1
return
				
      
    
  

